CREATE TABLE drama_catalogue(
    drama_id INT AUTO_INCREMENT PRIMARY KEY,
    image_filename VARCHAR(255) NOT NULL,
    title VARCHAR(100) NOT NULL,
    average_rating DECIMAL(3,2) NOT NULL,
    release_year INT NOT NULL CHECK (release_year >= 1900 AND release_year <= 2100),
    episodes TINYINT(2) NOT NULL,
    category VARCHAR(50) NOT NULL,
    original_network VARCHAR(50) NOT NULL,
    director VARCHAR(255) NOT NULL,
    screenwriter VARCHAR(255) NOT NULL,
    cast VARCHAR(255) NOT NULL, 
    description TEXT NOT NULL,
    image_url VARCHAR(2048) NOT NULL
);

CREATE TABLE catalogue_admin (
    account_id  INT(3) AUTO_INCREMENT PRIMARY KEY,
    username VARCHAR(16) NOT NULL,
    first_name VARCHAR(25) NOT NULL,
    last_name VARCHAR(25) NOT NULL, 
    email VARCHAR(255) NOT NULL,
    hashed_password VARCHAR(72) NOT NULL,
    profile_img VARCHAR(1024) DEFAULT NULL,
    date datetime DEFAULT current_timestamp(), 
    role VARCHAR(10) NOT NULL
);

CREATE TABLE user_rating (
    rating_id INT AUTO_INCREMENT PRIMARY KEY,
    account_id INT NOT NULL,
    drama_id INT NOT NULL,
    title VARCHAR(50) NOT NULL,
    rating TINYINT(1) NOT NULL CHECK (rating >= 0 AND rating <= 5),
    description TEXT,
    review_date DATETIME default current_timestamp(),
    FOREIGN KEY (account_id) REFERENCES catalogue_admin(account_id),
    FOREIGN KEY (drama_id) REFERENCES drama_catalogue(drama_id)
);

